mapper.insert:
INSERT INTO library(no, account_no, game_id, playtime, isnewbit
, fullgame_id
) VALUES( ( SELECT NVL(MAX(no) + 1, 1) FROM library ), ( SELECT no FROM account WHERE id = #{sessionId} ) , #{appId}, '0시간', 'N'
, #{fullgameId}
)

mapper.update:
UPDATE library
SET isshow='Y'
WHERE game_id = #{game_id}
AND account_no = #{account_no}
